mapper.update:
UPDATE t_device_firmware
SET download_count = download_count + 1
WHERE firmware_id = #{firmwareId}

UPDATE t_device_firmware
SET upgrade_count = upgrade_count + 1
WHERE firmware_id = #{firmwareId}

mapper.select:
SELECT * FROM t_device_firmware WHERE device_type = #{deviceType} AND firmware_status = 2 AND is_enabled = 1 AND deleted = 0
AND device_model = #{deviceModel}
ORDER BY firmware_version DESC
