mapper.select:
SELECT * FROM t_offline_whitelist
WHERE user_id = #{userId}
  AND enabled = 1
  AND effective_date <= #{currentTime}
  AND (expire_date IS NULL OR expire_date > #{currentTime})
ORDER BY whitelist_type, effective_date DESC

SELECT * FROM t_offline_whitelist
WHERE device_id = #{deviceId}
  AND enabled = 1
  AND effective_date <= #{currentTime}
  AND (expire_date IS NULL OR expire_date > #{currentTime})
ORDER BY whitelist_type, effective_date DESC

SELECT * FROM t_offline_whitelist
WHERE area_id = #{areaId}
  AND enabled = 1
  AND effective_date <= #{currentTime}
  AND (expire_date IS NULL OR expire_date > #{currentTime})
ORDER BY whitelist_type, effective_date DESC
