mapper.select:
SELECT *
FROM t_common_approval_statistics
WHERE business_type = #{businessType}
  AND statistics_date = #{statisticsDate}
  AND statistics_dimension = #{statisticsDimension}
  AND deleted_flag = 0
LIMIT 1

SELECT *
FROM t_common_approval_statistics
WHERE business_type = #{businessType}
  AND statistics_date BETWEEN #{startDate} AND #{endDate}
  AND statistics_dimension = #{statisticsDimension}
  AND deleted_flag = 0
ORDER BY statistics_date ASC
