mapper.select:
SELECT * FROM t_video_device_map
WHERE area_id = #{areaId}
  AND floor_level = #{floorLevel}
  AND deleted_flag = 0
  AND display_status != 0
ORDER BY device_name

SELECT * FROM t_video_device_map
WHERE map_image_id = #{mapImageId}
  AND x_coordinate BETWEEN #{minX} AND #{maxX}
  AND y_coordinate BETWEEN #{minY} AND #{maxY}
  AND deleted_flag = 0
  AND display_status = 1
ORDER BY x_coordinate, y_coordinate

SELECT * FROM t_video_device_map
WHERE map_image_id = #{mapImageId}
  AND deleted_flag = 0
  AND display_status = 1
  AND SQRT(POW(x_coordinate - #{xCoordinate}, 2) + POW(y_coordinate - #{yCoordinate}, 2)) <= #{radius}
ORDER BY SQRT(POW(x_coordinate - #{xCoordinate}, 2) + POW(y_coordinate - #{yCoordinate}, 2))
